import React, { useState } from 'react';
import { FiInfo } from 'react-icons/fi';
import './LinkForm.css';

function LinkForm({ onAddLink }) {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');
  const [iconBase64, setIconBase64] = useState('');
  const [category, setCategory] = useState('favorite');

  const isValidUrl = (value) => {
    try {
      new URL(value);
      return true;
    } catch {
      return false;
    }
  };

  const fetchWithTimeout = (checkUrl, options, timeout = 3000) => {
    return Promise.race([
      fetch(checkUrl, options),
      new Promise((_, reject) =>
        setTimeout(() => reject(new Error('timeout')), timeout)
      ),
    ]);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) {
      setIconBase64('');
      return;
    }
    const reader = new FileReader();
    reader.onload = (evt) => {
      const base64Data = evt.target.result;
      setIconBase64(base64Data);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (title.trim() === '' || url.trim() === '') return;
    if (!isValidUrl(url)) {
      alert('Пожалуйста, введите корректный URL, например, https://example.com');
      return;
    }
    try {
      await fetchWithTimeout(url, { method: 'HEAD', mode: 'no-cors' }, 3000);
      onAddLink({
        title,
        url,
        customIcon: iconBase64 || '',
        category: category.trim(),
        createdAt: Date.now()
      });
      setTitle('');
      setUrl('');
      setIconBase64('');
      setCategory('favorite');
      e.target.reset();
    } catch {
      alert('Сайт не найден или недоступен');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="link-form">
      <div className="input-group">
        <label>
          Название ссылки
          <span className="tooltip-icon">
            <FiInfo />
            <span className="tooltip-text">
              Введите название ссылки, как она будет отображаться.
            </span>
          </span>
        </label>
        <input
          type="text"
          placeholder="Введите название ссылки"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>

      <div className="input-group">
        <label>
          URL сайта
          <span className="tooltip-icon">
            <FiInfo />
            <span className="tooltip-text">
              Введите URL сайта, например, https://example.com
            </span>
          </span>
        </label>
        <input
          type="url"
          placeholder="https://example.com"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          required
        />
      </div>

      <div className="input-group">
        <label>
          Иконка
          <span className="tooltip-icon">
            <FiInfo />
            <span className="tooltip-text">
              Выберите изображение для иконки блока (необязательно).
            </span>
          </span>
        </label>
        <input type="file" accept="image/*" onChange={handleFileChange} />
      </div>

      <div className="input-group">
        <label>
          Категория
          <span className="tooltip-icon">
            <FiInfo />
            <span className="tooltip-text">
              Введите название категории. Для избранных введите "favorite".
            </span>
          </span>
        </label>
        <input
          type="text"
          placeholder="favorite или другое название"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />
      </div>

      <button type="submit">Добавить ссылку</button>
    </form>
  );
}

export default LinkForm;
