import React, { useState, useEffect, useRef } from 'react';
import './App.css';

import LinkBlock from './components/LinkBlock';
import SidebarMenu from './components/SidebarMenu';
import WeatherWidget from './components/WeatherWidget';
import ClockWidget from './components/ClockWidget';
import TodoWidget from './widgets/TodoWidget';

import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import groupBy from './utils/groupBy'; // см. utils ниже

function App() {
  const [theme, setTheme] = useState('light');

  // Ваш стейт links
  const [links, setLinks] = useState([]);
  const [isRemoveMode, setIsRemoveMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [sortBy, setSortBy] = useState('manual');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [editIndex, setEditIndex] = useState(null);

  const fileInputRef = useRef(null);

  // Загружаем тему и ссылки из localStorage
  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) setTheme(storedTheme);

    const storedLinks = localStorage.getItem('userLinks');
    if (storedLinks) {
      setLinks(JSON.parse(storedLinks));
    } else {
      // Пример начальных ссылок
      setLinks([
        { title: 'Google', url: 'https://google.com', customIcon: '', category: 'favorite', createdAt: Date.now() },
        { title: 'YouTube', url: 'https://youtube.com', customIcon: '', category: 'favorite', createdAt: Date.now() },
        { title: 'GitHub', url: 'https://github.com', customIcon: '', category: 'Рабочие', createdAt: Date.now() },
        { title: 'Netflix', url: 'https://netflix.com', customIcon: '', category: 'Развлекательные', createdAt: Date.now() },
      ]);
    }
  }, []);

  // Сохраняем тему и ссылки в localStorage
  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    localStorage.setItem('userLinks', JSON.stringify(links));
  }, [links]);

  const toggleTheme = () => {
    setTheme((prev) => (prev === 'light' ? 'dark' : 'light'));
  };

  const toggleRemoveMode = () => {
    setIsRemoveMode((prev) => !prev);
  };

  const handleAddLink = (newLink) => {
    // Проверка дубликатов
    const duplicate = links.some((link) => link.url === newLink.url);
    if (duplicate) {
      alert('Такая ссылка уже добавлена!');
      return;
    }
    setLinks((prev) => [...prev, newLink]);
  };

  const handleRemoveLink = (index) => {
    setLinks((prev) => prev.filter((_, i) => i !== index));
  };

  const handleMenuToggle = () => {
    setIsMenuOpen((prev) => !prev);
  };

  const handleEditLink = (index) => {
    setEditIndex(index);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
      fileInputRef.current.click();
    }
  };

  const handleSelectFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      const base64Data = evt.target.result;
      setLinks((prev) => {
        const updated = [...prev];
        updated[editIndex] = {
          ...updated[editIndex],
          customIcon: base64Data
        };
        return updated;
      });
    };
    reader.readAsDataURL(file);
  };

  // Фильтрация
  const filteredLinks = selectedCategory === 'All'
    ? links
    : links.filter((link) => link.category === selectedCategory);

  // Сортировка
  const sortedLinks = [...filteredLinks];
  if (sortBy === 'alphabetical') {
    sortedLinks.sort((a, b) => a.title.localeCompare(b.title, 'ru', { sensitivity: 'base' }));
  } else if (sortBy === 'date') {
    sortedLinks.sort((a, b) => b.createdAt - a.createdAt);
  }
  // manual => Drag & Drop

  // Drag & Drop
  const onDragEnd = (result) => {
    if (!result.destination) return;
    if (sortBy !== 'manual') return;
    if (selectedCategory !== 'All') return;
    const fromIndex = result.source.index;
    const toIndex = result.destination.index;

    // Переставляем ссылки в sortedLinks
    const item = sortedLinks[fromIndex];
    const newLinks = [...links];
    const globalIndex = links.findIndex((l) => l === item);
    newLinks.splice(globalIndex, 1);
    // Целевая ссылка
    const targetItem = sortedLinks[toIndex];
    const globalIndexTarget = links.findIndex((l) => l === targetItem);
    newLinks.splice(globalIndexTarget, 0, item);
    setLinks(newLinks);
  };

  // 1) Избранные (category === 'favorite')
  const favoriteLinks = sortedLinks.filter((link) => link.category === 'favorite');

  // 2) Остальные (category !== 'favorite')
  const otherLinks = sortedLinks.filter((link) => link.category !== 'favorite');

  // Группируем остальные по category
  // groupBy(otherLinks, (link) => link.category || 'Без категории')
  const grouped = groupBy(otherLinks, (link) => link.category || 'Без категории');
  // Превращаем объект в массив вида [{ name, items }, ...]
  const groupedCategories = Object.keys(grouped).map((catName) => ({
    name: catName,
    items: grouped[catName],
  }));

  return (
    <div className={`App ${theme}`}>
      <button className="menu-button" onClick={handleMenuToggle}>
        ☰
      </button>

      {/* Sidebar */}
      <SidebarMenu
        isOpen={isMenuOpen}
        onClose={handleMenuToggle}
        onAddLink={handleAddLink}
        toggleTheme={toggleTheme}
        toggleRemoveMode={toggleRemoveMode}
        sortBy={sortBy}
        setSortBy={setSortBy}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
        allCategories={['All', ...new Set(links.map((l) => l.category))]}
      />

      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        accept="image/*"
        onChange={handleSelectFile}
      />

      <main>
        {/* DragDropContext только если хотите drag & drop именно в одной зоне */}
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="favorites-droppable" direction="horizontal">
            {(provided) => (
              <section className="favorites-row" ref={provided.innerRef} {...provided.droppableProps}>
                <h2>Избранные</h2>
                <div className="favorites-links">
                  {favoriteLinks.map((link, index) => (
                    <Draggable
                      key={`fav-${index}`}
                      draggableId={`fav-draggable-${index}`}
                      index={index}
                      isDragDisabled={sortBy !== 'manual'}
                    >
                      {(providedDraggable) => (
                        <div
                          ref={providedDraggable.innerRef}
                          {...providedDraggable.draggableProps}
                          {...providedDraggable.dragHandleProps}
                        >
                          <LinkBlock
                            title={link.title}
                            url={link.url}
                            customIcon={link.customIcon}
                            isRemoveMode={isRemoveMode}
                            onRemove={() => {
                              const globalIndex = links.indexOf(link);
                              if (globalIndex >= 0) handleRemoveLink(globalIndex);
                            }}
                            onEdit={() => {
                              const globalIndex = links.indexOf(link);
                              if (globalIndex >= 0) handleEditLink(globalIndex);
                            }}
                          />
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              </section>
            )}
          </Droppable>
        </DragDropContext>

        {/* Ниже — сетка категорий */}
        <section className="categories-grid">
          {groupedCategories.map((catBlock) => (
            <div key={catBlock.name} className="category-section">
              <h3>{catBlock.name}</h3>
              <div className="category-links">
                {catBlock.items.map((link, idx) => (
                  <LinkBlock
                    key={`cat-${catBlock.name}-${idx}`}
                    title={link.title}
                    url={link.url}
                    customIcon={link.customIcon}
                    isRemoveMode={isRemoveMode}
                    onRemove={() => {
                      const globalIndex = links.indexOf(link);
                      if (globalIndex >= 0) handleRemoveLink(globalIndex);
                    }}
                    onEdit={() => {
                      const globalIndex = links.indexOf(link);
                      if (globalIndex >= 0) handleEditLink(globalIndex);
                    }}
                  />
                ))}
              </div>
            </div>
          ))}
        </section>

        <section className="widgets-section">
          <WeatherWidget theme={theme} />
          <ClockWidget />
          <TodoWidget theme={theme} />
        </section>
      </main>
    </div>
  );
}

export default App;
