import React, { useState } from 'react';
import './LinkForm.css';

function LinkForm({ onAddLink }) {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');
  const [icon, setIcon] = useState(''); // Новое состояние для иконки

  // Проверка корректности URL с помощью конструктора URL
  const isValidUrl = (value) => {
    try {
      new URL(value);
      return true;
    } catch (error) {
      return false;
    }
  };

  // Функция для выполнения fetch с тайм-аутом (для проверки сайта)
  const fetchWithTimeout = (url, options, timeout = 3000) => {
    return Promise.race([
      fetch(url, options),
      new Promise((_, reject) =>
        setTimeout(() => reject(new Error('timeout')), timeout)
      )
    ]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (title.trim() === '' || url.trim() === '') return;

    // Проверяем валидность URL для блока
    if (!isValidUrl(url)) {
      alert('Пожалуйста, введите корректный URL, например, https://example.com');
      return;
    }

    // Проверяем сайт на доступность
    try {
      const response = await fetchWithTimeout(url, {
        method: 'HEAD',
        mode: 'no-cors'
      }, 3000);

      if (response && (response.ok || response.type === 'opaque')) {
        // Если пользователь указал свою иконку, можно также проверить, 
        // корректна ли она (необязательно, но при желании):
        // if (icon && !isValidUrl(icon)) { ... }

        onAddLink({ 
          title, 
          url, 
          customIcon: icon.trim() || '' // Передаём ссылку на иконку (если есть)
        });
        setTitle('');
        setUrl('');
        setIcon('');
      } else {
        alert('Сайт не найден или недоступен');
      }
    } catch (error) {
      alert('Сайт не найден или недоступен');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="link-form">
      <input
        type="text"
        placeholder="Название ссылки"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <input
        type="url"
        placeholder="URL (например, https://example.com)"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        required
      />

      {/* Новое поле для пользовательской иконки (необязательное) */}
      <input
        type="url"
        placeholder="URL иконки (необязательно)"
        value={icon}
        onChange={(e) => setIcon(e.target.value)}
      />

      <button type="submit">Добавить ссылку</button>
    </form>
  );
}

export default LinkForm;
