import React, { useState } from 'react';
import './LinkForm.css';

function LinkForm({ onAddLink }) {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  // Проверка корректности URL с помощью конструктора URL
  const isValidUrl = (value) => {
    try {
      new URL(value);
      return true;
    } catch (error) {
      return false;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (title.trim() === '' || url.trim() === '') return;

    if (!isValidUrl(url)) {
      alert('Пожалуйста, введите корректный URL, например, https://example.com');
      return;
    }

    try {
      // Выполняем HEAD-запрос для проверки доступности сайта.
      // Режим 'no-cors' используется, чтобы избежать проблем с CORS, но он возвращает "opaque" ответ.
      const response = await fetch(url, {
        method: 'HEAD',
        mode: 'no-cors'
      });

      // Если режим no-cors, то тип ответа будет 'opaque'. В этом случае мы не можем проверить status,
      // но если fetch завершился без исключений, можно предположить, что сайт существует.
      if (response && response.type === 'opaque') {
        onAddLink({ title, url });
        setTitle('');
        setUrl('');
      } else if (response && response.ok) {
        onAddLink({ title, url });
        setTitle('');
        setUrl('');
      } else {
        alert('Сайт не найден или недоступен');
      }
    } catch (error) {
      // Если запрос завершился с ошибкой, выводим уведомление.
      alert('Сайт не найден или недоступен');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="link-form">
      <input
        type="text"
        placeholder="Название ссылки"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <input
        type="url"
        placeholder="URL (например, https://example.com)"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        required
      />
      <button type="submit">Добавить ссылку</button>
    </form>
  );
}

export default LinkForm;
