import React, { useState, useEffect } from 'react';

function WeatherWidget() {
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    // Пример использования геолокации
    navigator.geolocation.getCurrentPosition(position => {
      // Здесь можно сделать запрос к API погоды, используя координаты
      // Пока для демонстрации установим статические данные
      setWeather({ temperature: 25, condition: 'Солнечно' });
    }, error => {
      console.error('Ошибка получения геолокации:', error);
      setWeather({ temperature: '-', condition: 'Нет данных' });
    });
  }, []);

  return (
    <div className="weather-widget widget">
      {weather ? (
        <>
          <p>{weather.temperature}°C</p>
          <p>{weather.condition}</p>
        </>
      ) : (
        <p>Загрузка погоды...</p>
      )}
    </div>
  );
}

export default WeatherWidget;
