import React from 'react';
import './LinkBlock.css';
import defaultIcon from '../assets/default-icon.png'; // если есть fallback

function LinkBlock({ title, url, customIcon, isRemoveMode, onRemove, onEdit }) {
  let domain = '';
  try {
    domain = new URL(url).hostname;
  } catch {}

  // Google favicon (или можно какой-то сервис)
  const googleIconUrl = `https://www.google.com/s2/favicons?domain=${domain}&sz=128`;
  const iconSrc = customIcon || googleIconUrl;

  // Фallback, если загрузка иконки не удалась
  const handleImgError = (e) => {
    e.currentTarget.src = defaultIcon;
  };

  return (
    <div className="link-block-wrapper">
      <a
        href={url}
        className="link-block"
        target="_blank"
        rel="noopener noreferrer"
      >
        <div className="icon-wrapper">
          <img
            src={iconSrc}
            alt="icon"
            className="favicon"
            onError={handleImgError}
          />
        </div>
        <div className="tooltip">{title}</div>
      </a>

      {isRemoveMode && (
        <>
          <button className="remove-block-btn" onClick={onRemove}>
            -
          </button>
          <button className="edit-block-btn" onClick={onEdit}>
            ✎
          </button>
        </>
      )}
    </div>
  );
}

export default LinkBlock;
