import React from 'react';
import './LinkBlock.css';

function LinkBlock({ title, url }) {
  // Извлекаем домен из URL для получения favicon
  let domain = '';
  try {
    domain = new URL(url).hostname;
  } catch (error) {
    console.error('Неверный URL:', error);
  }
  const faviconUrl = `https://www.google.com/s2/favicons?domain=${domain}`;

  return (
    <a href={url} className="link-block" target="_blank" rel="noopener noreferrer">
      <div className="link-block-icon">
        <img src={faviconUrl} alt={`${domain} icon`} />
      </div>
      <div className="link-block-tooltip">
        {title}
      </div>
    </a>
  );
}

export default LinkBlock;
