import React from 'react';
import './LinkBlock.css';

function LinkBlock({ title, url }) {
  // Получаем доменное имя из URL
  let domain;
  try {
    domain = new URL(url).hostname;
  } catch (e) {
    domain = ''; // если URL некорректен
  }

  // Формируем URL для favicon
  const iconUrl = `https://www.google.com/s2/favicons?domain=${domain}`;

  return (
    <a
      href={url}
      className="link-block"
      target="_blank"
      rel="noopener noreferrer"
    >
      <div className="icon-wrapper">
        <img src={iconUrl} alt="favicon" className="favicon" />
        {/* Всплывающая подсказка с заголовком */}
        <div className="tooltip">{title}</div>
      </div>
    </a>
  );
}

export default LinkBlock;
