import React, { useState, useEffect, useRef } from 'react';
import { FiMoreHorizontal } from 'react-icons/fi';
import './App.css';

import LinkBlock from './components/LinkBlock';
import SidebarMenu from './components/SidebarMenu';
import WeatherWidget from './components/WeatherWidget';
import ClockWidget from './components/ClockWidget';
import TodoWidget from './components/widgets/TodoWidget';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function groupBy(array, keyFn) {
  const result = {};
  array.forEach((item) => {
    const key = keyFn(item);
    if (!result[key]) {
      result[key] = [];
    }
    result[key].push(item);
  });
  return result;
}

function App() {
  const [theme, setTheme] = useState('light');
  const [links, setLinks] = useState([]);
  const [isRemoveMode, setIsRemoveMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [sortBy, setSortBy] = useState('manual');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [editIndex, setEditIndex] = useState(null);
  const fileInputRef = useRef(null);
  const [folderOpenCat, setFolderOpenCat] = useState(null);

  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) setTheme(storedTheme);

    const storedLinks = localStorage.getItem('userLinks');
    if (storedLinks) {
      setLinks(JSON.parse(storedLinks));
    } else {
      setLinks([
        {
          title: 'Google',
          url: 'https://www.google.com',
          customIcon: '',
          category: 'favorite',
          createdAt: Date.now(),
        },
        {
          title: 'YouTube',
          url: 'https://www.youtube.com',
          customIcon: '',
          category: '',
          createdAt: Date.now(),
        },
      ]);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    localStorage.setItem('userLinks', JSON.stringify(links));
  }, [links]);

  useEffect(() => {
    document.documentElement.classList.remove('light','dark');
    document.documentElement.classList.add(theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prev) => (prev === 'light' ? 'dark' : 'light'));
  };

  const toggleRemoveMode = () => {
    setIsRemoveMode((prev) => !prev);
  };

  const handleAddLink = (newLink) => {
    const isDuplicate = links.some((link) => link.url === newLink.url);
    if (isDuplicate) {
      alert('Такая ссылка уже добавлена!');
      return;
    }
    setLinks((prev) => [...prev, newLink]);
  };

  const handleRemoveLink = (index) => {
    setLinks((prev) => prev.filter((_, i) => i !== index));
  };

  const handleMenuToggle = () => {
    setIsMenuOpen((prev) => !prev);
  };

  const handleEditLink = (index) => {
    setEditIndex(index);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
      fileInputRef.current.click();
    }
  };

  const handleSelectFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      const base64Data = evt.target.result;
      setLinks((prev) => {
        const updated = [...prev];
        updated[editIndex] = {
          ...updated[editIndex],
          customIcon: base64Data,
        };
        return updated;
      });
    };
    reader.readAsDataURL(file);
  };

  let filteredLinks;
  if (selectedCategory === 'All') {
    filteredLinks = links;
  } else {
    filteredLinks = links.filter(
      (l) => l.category === 'favorite' || l.category === selectedCategory
    );
  }

  const sortedLinks = [...filteredLinks];
  if (sortBy === 'alphabetical') {
    sortedLinks.sort((a, b) =>
      a.title.localeCompare(b.title, 'ru', { sensitivity: 'base' })
    );
  } else if (sortBy === 'date') {
    sortedLinks.sort((a, b) => b.createdAt - a.createdAt);
  }

  const onDragEnd = (result) => {
    if (!result.destination) return;
    if (sortBy !== 'manual') return;
    const { source, destination } = result;
    if (source.droppableId === 'favorites' && destination.droppableId === 'favorites') {
      reorderFavorites(source.index, destination.index);
    }
    if (source.droppableId.startsWith('folder:') && destination.droppableId.startsWith('folder:')) {
      if (source.droppableId === destination.droppableId) {
        reorderFolder(source.droppableId, source.index, destination.index);
      }
    }
  };

  const reorderFavorites = (fromIndex, toIndex) => {
    setLinks((prev) => {
      const newArr = [...prev];
      const favorites = newArr.filter((l) => l.category === 'favorite');
      if (fromIndex < 0 || fromIndex >= favorites.length) return newArr;
      if (toIndex < 0 || toIndex >= favorites.length) return newArr;
      const favIndices = favorites.map((f) => newArr.indexOf(f));
      const itemIndex = favIndices[fromIndex];
      const [removed] = newArr.splice(itemIndex, 1);
      const targetIndex = favIndices[toIndex];
      newArr.splice(targetIndex, 0, removed);
      return newArr;
    });
  };

  const reorderFolder = (droppableId, fromIndex, toIndex) => {
    const folderCat = droppableId.replace('folder:', '');
    setLinks((prev) => {
      const newArr = [...prev];
      const folderLinks = newArr.filter((l) => l.category === folderCat);
      if (!folderLinks.length) return newArr;
      if (fromIndex < 0 || fromIndex >= folderLinks.length) return newArr;
      if (toIndex < 0 || toIndex >= folderLinks.length) return newArr;
      const folderIndices = folderLinks.map((f) => newArr.indexOf(f));
      const itemIndex = folderIndices[fromIndex];
      const [removed] = newArr.splice(itemIndex, 1);
      const targetIndex = folderIndices[toIndex];
      newArr.splice(targetIndex, 0, removed);
      return newArr;
    });
  };

  const favoriteLinks = sortedLinks.filter((l) => l.category === 'favorite');
  const otherLinks = sortedLinks.filter((l) => l.category !== 'favorite');
  const grouped = groupBy(otherLinks, (l) => l.category || 'Без категории');
  const groupedCategories = Object.keys(grouped).map((catName) => ({
    name: catName,
    items: grouped[catName],
  }));

  const renderFavoriteLinks = () => {
    return favoriteLinks.map((link, index) => (
      <Draggable
        key={`fav-${index}`}
        draggableId={`fav-${index}`}
        index={index}
        isDragDisabled={sortBy !== 'manual'}
      >
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            style={provided.draggableProps.style}
          >
            <LinkBlock
              title={link.title}
              url={link.url}
              customIcon={link.customIcon}
              isRemoveMode={isRemoveMode}
              onRemove={() => {
                const globalIndex = links.indexOf(link);
                if (globalIndex >= 0) handleRemoveLink(globalIndex);
              }}
              onEdit={() => {
                const globalIndex = links.indexOf(link);
                if (globalIndex >= 0) handleEditLink(globalIndex);
              }}
            />
          </div>
        )}
      </Draggable>
    ));
  };

  const renderCategories = () => {
    return groupedCategories.map((catBlock) => {
      const firstThree = catBlock.items.slice(0, 3);
      const hasMore = catBlock.items.length > 3;
      return (
        <div key={catBlock.name} className="category-section">
          <h3>{catBlock.name}</h3>
          <div className="category-links">
            {firstThree.map((link, idx) => (
              <LinkBlock
                key={`cat-${catBlock.name}-${idx}`}
                title={link.title}
                url={link.url}
                customIcon={link.customIcon}
                isRemoveMode={isRemoveMode}
                onRemove={() => {
                  const globalIndex = links.indexOf(link);
                  if (globalIndex >= 0) handleRemoveLink(globalIndex);
                }}
                onEdit={() => {
                  const globalIndex = links.indexOf(link);
                  if (globalIndex >= 0) handleEditLink(globalIndex);
                }}
              />
            ))}
            {hasMore && (
              <div
                className="link-block-wrapper folder-block"
                onClick={() => setFolderOpenCat(catBlock.name)}
              >
                <div className="link-block folder-block-inner">
                  <div className="icon-wrapper">
                    <FiMoreHorizontal className="folder-more-icon" />
                  </div>
                  <div className="tooltip">Показать ещё</div>
                </div>
              </div>
            )}
          </div>
        </div>
      );
    });
  };

  const openFolderItems = folderOpenCat
    ? groupedCategories.find((c) => c.name === folderOpenCat)?.items
    : null;

  const renderFolderModal = () => {
    if (!folderOpenCat) return null;
    return (
      <div className="folder-modal-overlay" onClick={() => setFolderOpenCat(null)}>
        <div className="folder-modal-content" onClick={(e) => e.stopPropagation()}>
          <button className="folder-close" onClick={() => setFolderOpenCat(null)}>
            ×
          </button>
          <h2>Категория: {folderOpenCat}</h2>
          <Droppable droppableId={`folder:${folderOpenCat}`} direction="horizontal">
            {(provided) => (
              <div className="folder-links" ref={provided.innerRef} {...provided.droppableProps}>
                {openFolderItems?.map((link, idx) => (
                  <Draggable
                    key={`folder-${folderOpenCat}-${idx}`}
                    draggableId={`folder-${folderOpenCat}-${idx}`}
                    index={idx}
                    isDragDisabled={sortBy !== 'manual'}
                  >
                    {(providedDrag) => (
                      <div
                        ref={providedDrag.innerRef}
                        {...providedDrag.draggableProps}
                        {...providedDrag.dragHandleProps}
                        style={providedDrag.draggableProps.style}
                      >
                        <LinkBlock
                          title={link.title}
                          url={link.url}
                          customIcon={link.customIcon}
                          isRemoveMode={isRemoveMode}
                          onRemove={() => {
                            const globalIndex = links.indexOf(link);
                            if (globalIndex >= 0) handleRemoveLink(globalIndex);
                          }}
                          onEdit={() => {
                            const globalIndex = links.indexOf(link);
                            if (globalIndex >= 0) handleEditLink(globalIndex);
                          }}
                        />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </div>
    );
  };

  const allCategories = ['All', ...new Set(links.map((l) => l.category).filter((c) => c && c !== 'favorite'))];

  return (
    <div className={`App ${theme}`}>
      <div>
        <input
          type="checkbox"
          id="checkbox"
          className="menu-btn"
          onClick={handleMenuToggle}
        />
        <label htmlFor="checkbox" className="toggle">
          <div className="bars" id="bar1" />
          <div className="bars" id="bar2" />
          <div className="bars" id="bar3" />
        </label>
      </div>

      <SidebarMenu
        isOpen={isMenuOpen}
        onClose={handleMenuToggle}
        onAddLink={handleAddLink}
        toggleTheme={toggleTheme}
        toggleRemoveMode={toggleRemoveMode}
      />

      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        accept="image/*"
        onChange={handleSelectFile}
      />

      <main>
        <DragDropContext onDragEnd={onDragEnd}>

          <Droppable droppableId="favorites" direction="horizontal">
            {(provided) => (
              <section className="favorites-row" ref={provided.innerRef} {...provided.droppableProps}>
                <h2>Избранные</h2>
                <div className="favorites-links">
                  {renderFavoriteLinks()}
                  {provided.placeholder}
                </div>
              </section>
            )}
          </Droppable>

          <section className="sorting-category-panel">
            <div className="sort-block">
              <h4>Сортировка</h4>
              <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                <option value="manual">Ручная (Drag & Drop)</option>
                <option value="alphabetical">По алфавиту</option>
                <option value="date">По дате (новые сверху)</option>
              </select>
            </div>

            <div className="category-block">
              <h4>Категория</h4>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {allCategories.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>
          </section>

          <section className="categories-grid">
            {renderCategories()}
          </section>
        </DragDropContext>

        <section className="widgets-section">
          <WeatherWidget theme={theme} />
          <ClockWidget />
          <TodoWidget theme={theme} />
        </section>
      </main>

      {renderFolderModal()}
    </div>
  );
}

export default App;
