import React from 'react';
import './LinkBlock.css';

function LinkBlock({ title, url, isRemoveMode, onRemove }) {
  let domain;
  try {
    domain = new URL(url).hostname;
  } catch (e) {
    domain = '';
  }

  const iconUrl = `https://www.google.com/s2/favicons?domain=${domain}&sz=128`;

  return (
    <div className="link-block-wrapper">
      <a
        href={url}
        className="link-block"
        target="_blank"
        rel="noopener noreferrer"
      >
        <div className="icon-wrapper">
          <img src={iconUrl} alt="favicon" className="favicon" />
        </div>
        <div className="tooltip">{title}</div>
      </a>

      {/* Кнопка удаления: показываем, если isRemoveMode = true */}
      {isRemoveMode && (
        <button className="remove-block-btn" onClick={onRemove}>
          -
        </button>
      )}
    </div>
  );
}

export default LinkBlock;
