import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import LinkBlock from './components/LinkBlock';
import SidebarMenu from './components/SidebarMenu';
import WeatherWidget from './components/WeatherWidget';
import ClockWidget from './components/ClockWidget';
import TodoWidget from './components/widgets/TodoWidget';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function App() {
  const [theme, setTheme] = useState('light');
  const [links, setLinks] = useState([]);
  const [isRemoveMode, setIsRemoveMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [sortBy, setSortBy] = useState('manual');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [editIndex, setEditIndex] = useState(null);

  const fileInputRef = useRef(null);

  // Загружаем тему и ссылки из localStorage
  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) setTheme(storedTheme);

    const storedLinks = localStorage.getItem('userLinks');
    if (storedLinks) {
      setLinks(JSON.parse(storedLinks));
    } else {
      setLinks([
        { title: 'Google', url: 'https://www.google.com' },
        { title: 'YouTube', url: 'https://www.youtube.com' }
      ]);
    }
  }, []);

  // Сохраняем тему и ссылки в localStorage
  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    localStorage.setItem('userLinks', JSON.stringify(links));
  }, [links]);

  // Переключатель темы
  const toggleTheme = () => {
    setTheme(prev => (prev === 'light' ? 'dark' : 'light'));
  };

  // Режим удаления/редактирования
  const toggleRemoveMode = () => {
    setIsRemoveMode(prev => !prev);
  };

  // Добавление новой ссылки (с проверкой дубликатов)
  const handleAddLink = (newLink) => {
    const isDuplicate = links.some(link => link.url === newLink.url);
    if (isDuplicate) {
      alert('Такая ссылка уже добавлена!');
      return;
    }
    setLinks(prevLinks => [...prevLinks, newLink]);
  };

  // Удаление ссылки
  const handleRemoveLink = (index) => {
    setLinks(prevLinks => prevLinks.filter((_, i) => i !== index));
  };

  // Открытие/закрытие бокового меню
  const handleMenuToggle = () => {
    setIsMenuOpen(prev => !prev);
  };

  // Редактирование иконки (customIcon)
  const handleEditLink = (index) => {
    setEditIndex(index);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
      fileInputRef.current.click();
    }
  };

  // Выбор файла для customIcon
  const handleSelectFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      const base64Data = evt.target.result;
      setLinks(prevLinks => {
        const updated = [...prevLinks];
        updated[editIndex] = {
          ...updated[editIndex],
          customIcon: base64Data
        };
        return updated;
      });
    };
    reader.readAsDataURL(file);
  };

  // Фильтрация по категории
  const filteredLinks = selectedCategory === 'All'
    ? links
    : links.filter(link => link.category === selectedCategory);

  // Сортировка
  const sortedLinks = [...filteredLinks];
  if (sortBy === 'alphabetical') {
    sortedLinks.sort((a, b) => a.title.localeCompare(b.title, 'ru', { sensitivity: 'base' }));
  } else if (sortBy === 'date') {
    // Для этого нужно иметь дату добавления, если есть
    // sortedLinks.sort((a, b) => b.createdAt - a.createdAt);
  }

  // Drag & Drop
  const onDragEnd = (result) => {
    if (!result.destination) return;
    if (sortBy !== 'manual') return;
    if (selectedCategory !== 'All') return;

    const fromIndex = result.source.index;
    const toIndex = result.destination.index;
    const movingItem = sortedLinks[fromIndex];

    const newLinks = Array.from(links);
    const globalIndex = links.findIndex(l => l === movingItem);
    newLinks.splice(globalIndex, 1);

    const targetItem = sortedLinks[toIndex];
    const globalIndexTarget = links.findIndex(l => l === targetItem);
    newLinks.splice(globalIndexTarget, 0, movingItem);

    setLinks(newLinks);
  };

  return (
    <div className={`App ${theme}`}>
      <button className="menu-button" onClick={handleMenuToggle}>
        ☰
      </button>

      <SidebarMenu
        isOpen={isMenuOpen}
        onClose={handleMenuToggle}
        onAddLink={handleAddLink}
        toggleTheme={toggleTheme}
        toggleRemoveMode={toggleRemoveMode}
        sortBy={sortBy}
        setSortBy={setSortBy}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
      />

      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        accept="image/*"
        onChange={handleSelectFile}
      />

      <main>
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="links-droppable" direction="horizontal">
            {(provided) => (
              <section
                className="links-section"
                ref={provided.innerRef}
                {...provided.droppableProps}
              >
                {sortedLinks.map((link, index) => (
                  <Draggable
                    key={index}
                    draggableId={`link-${index}`}
                    index={index}
                    isDragDisabled={sortBy !== 'manual' || selectedCategory !== 'All'}
                  >
                    {(provided2) => (
                      <div
                        ref={provided2.innerRef}
                        {...provided2.draggableProps}
                        {...provided2.dragHandleProps}
                      >
                        <LinkBlock
                          key={index}
                          title={link.title}
                          url={link.url}
                          customIcon={link.customIcon}  // если есть
                          isRemoveMode={isRemoveMode}
                          onRemove={() => handleRemoveLink(index)}
                          onEdit={() => handleEditLink(index)}
                        />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </section>
            )}
          </Droppable>
        </DragDropContext>

        <section className="widgets-section">
          <WeatherWidget />
          <ClockWidget />
          <TodoWidget />
        </section>
      </main>
    </div>
  );
}

export default App;
