import React, { useRef } from 'react';
import './BackgroundModal.css';

function BackgroundModal({
  show,
  onClose,
  useDynamicTimeTheme,
  setUseDynamicTimeTheme,
  onLocalBackgroundFile,
  resetBackground
}) {
  const fileRef = useRef(null);

  if (!show) return null;

  const handleOverlayClick = () => {
    onClose();
  };

  const handleContentClick = (e) => {
    e.stopPropagation();
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    onLocalBackgroundFile(file);
  };

  const handleSelectFile = () => {
    if (fileRef.current) fileRef.current.click();
  };

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className="modal-content" onClick={handleContentClick}>
        <button className="modal-close" onClick={onClose}>
          ×
        </button>
        <h2>Настройка фона</h2>

        <div className="bg-buttons">
          <button onClick={handleSelectFile}>Выбрать локальный файл</button>
          <button onClick={resetBackground}>Сбросить фон</button>
        </div>

        <hr />

        <h3>Автосмена темы</h3>
        <label>
          <input
            type="checkbox"
            checked={useDynamicTimeTheme}
            onChange={(e) => setUseDynamicTimeTheme(e.target.checked)}
          />
          По времени суток (7:00 - 19:00 Светлая, иначе Тёмная)
        </label>

        <input
          type="file"
          ref={fileRef}
          style={{ display: 'none' }}
          accept="image/*,video/*"
          onChange={handleFileChange}
        />
      </div>
    </div>
  );
}

export default BackgroundModal;
