import React, { useState } from 'react';
import LinkForm from './LinkForm';
import './SidebarMenu.css';

function SidebarMenu({
  isOpen,
  onClose,
  onAddLink,
  toggleTheme,
  toggleRemoveMode,
  sortBy,
  setSortBy,
  selectedCategory,
  setSelectedCategory,
  allCategories,

  customBackgroundType,
  handleBackgroundTypeChange,
  customBackgroundSrc,
  handleBackgroundSrcChange,
  useDynamicTimeTheme,
  handleDynamicTimeTheme
}) {
  const [showForm, setShowForm] = useState(false);

  const handleAddBlockClick = () => {
    setShowForm((prev) => !prev);
  };

  // Выбор готового варианта из галереи
  const handlePredefinedSelect = (bg) => {
    handleBackgroundTypeChange(bg.type);
    handleBackgroundSrcChange(bg.src);
  };

  return (
    <div className={`sidebar-menu ${isOpen ? 'open' : ''}`}>
      <button className="close-button" onClick={onClose}>
        &times;
      </button>
      <h2>Меню</h2>

      <button onClick={handleAddBlockClick}>Добавить блок</button>
      {showForm && (
        <div className="add-form-wrapper">
          <LinkForm onAddLink={onAddLink} />
        </div>
      )}

      <button onClick={toggleTheme}>Смена темы</button>
      <button onClick={toggleRemoveMode}>Изменение блока</button>

      <h4>Сортировка</h4>
      <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
        <option value="manual">Ручная (Drag & Drop)</option>
        <option value="alphabetical">По алфавиту</option>
        <option value="date">По дате (новые сверху)</option>
      </select>

      <h4>Категория</h4>
      <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
        <option value="All">All</option>
        {/* Тут allCategories передаётся, если хотите показывать их, 
            но у нас сейчас хранится набор готовых бэкграундов. 
            Если хотите именно категории линков, вставьте свой массив: */}
      </select>

      <hr />
      <h4>Фон:</h4>
      <div className="bg-group">
        <label>
          <input
            type="radio"
            name="bgType"
            value="default"
            checked={customBackgroundType === 'default'}
            onChange={() => handleBackgroundTypeChange('default')}
          />
          По умолчанию
        </label>
        <label>
          <input
            type="radio"
            name="bgType"
            value="image"
            checked={customBackgroundType === 'image'}
            onChange={() => handleBackgroundTypeChange('image')}
          />
          Картинка
        </label>
        <label>
          <input
            type="radio"
            name="bgType"
            value="video"
            checked={customBackgroundType === 'video'}
            onChange={() => handleBackgroundTypeChange('video')}
          />
          Видео
        </label>
      </div>

      {customBackgroundType !== 'default' && (
        <div className="bg-input">
          <p>URL / Ссылка:</p>
          <input
            type="text"
            value={customBackgroundSrc}
            onChange={(e) => handleBackgroundSrcChange(e.target.value)}
            placeholder="Вставьте ссылку на видео/картинку"
          />
        </div>
      )}

      <div className="bg-predefined">
        <p>Готовые варианты:</p>
        {/* allCategories здесь условно хранит наши готовые фоны */}
        {allCategories.map((bg, idx) => (
          <button key={idx} onClick={() => handlePredefinedSelect(bg)}>
            {bg.label}
          </button>
        ))}
      </div>

      <hr />
      <label>
        <input
          type="checkbox"
          checked={useDynamicTimeTheme}
          onChange={(e) => handleDynamicTimeTheme(e.target.checked)}
        />
        Автосмена темы по времени суток
      </label>
    </div>
  );
}

export default SidebarMenu;
