import React, { useState } from 'react';
import { FiInfo } from 'react-icons/fi';
import './LinkForm.css';

function LinkForm({ onAddLink }) {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');
  const [customIcon, setCustomIcon] = useState('');
  const [category, setCategory] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddLink({
      title,
      url,
      customIcon,
      category,
      createdAt: Date.now(),
    });
    setTitle('');
    setUrl('');
    setCustomIcon('');
    setCategory('');
  };

  return (
    <form className="link-form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label>
          Название ссылки
          <span className="info-icon" data-tooltip="Введите название ссылки, например 'Google'">
            <FiInfo />
          </span>
        </label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label>
          Ссылка
          <span className="info-icon" data-tooltip="Введите URL ссылки, например 'https://www.google.com'">
            <FiInfo />
          </span>
        </label>
        <input
          type="url"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label>
          Иконка (не обязательно)
          <span className="info-icon" data-tooltip="Можно вставить URL изображения или оставить пустым">
            <FiInfo />
          </span>
        </label>
        <input
          type="text"
          value={customIcon}
          onChange={(e) => setCustomIcon(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>
          Категория
          <span className="info-icon" data-tooltip="Введите название категории. Для избранных введите 'favorite'">
            <FiInfo />
          </span>
        </label>
        <input
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        />
      </div>
      <button type="submit">Добавить ссылку</button>
    </form>
  );
}

export default LinkForm;
