/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react';
import './WeatherWidget.css';
import {
  IoMdSunny,
  IoMdCloudy,
  IoMdRainy,
  IoMdSnow,
  IoMdThunderstorm,
  IoMdPartlySunny,
  IoMdMoon
} from 'react-icons/io';

function getWeatherIcon(description) {
  if (!description) return IoMdPartlySunny;
  const desc = description.toLowerCase();
  if (desc.includes('clear')) return IoMdSunny;
  if (desc.includes('cloud')) return IoMdCloudy;
  if (desc.includes('rain') || desc.includes('drizzle')) return IoMdRainy;
  if (desc.includes('snow')) return IoMdSnow;
  if (desc.includes('thunder')) return IoMdThunderstorm;
  if (desc.includes('wind') || desc.includes('breeze')) return IoMdPartlySunny;
  if (desc.includes('night') || desc.includes('moon')) return IoMdMoon;
  return IoMdPartlySunny;
}

function WeatherWidget({ theme }) {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [cityInput, setCityInput] = useState('');
  const API_KEY = '4cc9911b0a2723050929b73c2a59e727';

  useEffect(() => {
    const storedCity = localStorage.getItem('weatherCity') || '';
    setCityInput(storedCity);

    // Если нет geolocation
    if (!navigator.geolocation) {
      if (storedCity) {
        fetchWeatherByCity(storedCity);
      } else {
        setWeather(null);
      }
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        fetchWeatherByCoords(latitude, longitude);
      },
      (err) => {
        if (storedCity) {
          fetchWeatherByCity(storedCity);
        } else {
          setWeather(null);
        }
        setLoading(false);
      }
    );
  }, []);

  const fetchWeatherByCoords = async (lat, lon) => {
    try {
      setLoading(true);
      const res = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=metric&lang=ru&appid=${API_KEY}`);
      if (!res.ok) throw new Error('Не удалось получить погоду');
      const data = await res.json();
      setWeather({
        temp: Math.round(data.main.temp),
        description: data.weather[0].description,
        name: data.name,
      });
      // forecast
      const fr = await fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lon}&units=metric&lang=ru&appid=${API_KEY}`);
      if (!fr.ok) throw new Error('Не удалось получить прогноз');
      const fdata = await fr.json();
      setForecast(processForecastData(fdata.list));
    } catch (err) {
      alert(err.message);
      setWeather(null);
      setForecast([]);
    }
    setLoading(false);
  };

  const fetchWeatherByCity = async (cityName) => {
    try {
      setLoading(true);
      const res = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(cityName)}&units=metric&lang=ru&appid=${API_KEY}`);
      if (!res.ok) throw new Error('Город не найден');
      const data = await res.json();
      setWeather({
        temp: Math.round(data.main.temp),
        description: data.weather[0].description,
        name: data.name,
      });
      // forecast
      const fr = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${encodeURIComponent(cityName)}&units=metric&lang=ru&appid=${API_KEY}`);
      if (!fr.ok) throw new Error('Не удалось получить прогноз');
      const fdata = await fr.json();
      setForecast(processForecastData(fdata.list));
    } catch (err) {
      alert(err.message);
      setWeather(null);
      setForecast([]);
    }
    setLoading(false);
  };

  const processForecastData = (list) => {
    const dayMap = {};
    list.forEach((item) => {
      const dateTxt = item.dt_txt.split(' ')[0];
      const t = item.main.temp;
      if (!dayMap[dateTxt]) {
        dayMap[dateTxt] = { min: t, max: t };
      } else {
        if (t < dayMap[dateTxt].min) dayMap[dateTxt].min = t;
        if (t > dayMap[dateTxt].max) dayMap[dateTxt].max = t;
      }
    });
    const entries = Object.keys(dayMap).map((date) => ({
      date,
      min: Math.round(dayMap[date].min),
      max: Math.round(dayMap[date].max),
    }));
    entries.sort((a, b) => (a.date > b.date ? 1 : -1));
    return entries.slice(0, 4);
  };

  const handleWidgetClick = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => setIsModalOpen(false);

  const handleCitySubmit = (e) => {
    e.preventDefault();
    if (cityInput.trim()) {
      localStorage.setItem('weatherCity', cityInput.trim());
      fetchWeatherByCity(cityInput.trim());
    }
  };

  if (loading) {
    return (
      <div className="weather-widget mac-widget">
        <p>Загрузка погоды...</p>
      </div>
    );
  }

  if (!weather) {
    return (
      <>
        <div className="weather-widget mac-widget" onClick={handleWidgetClick}>
          <p>Погода недоступна</p>
        </div>
        {isModalOpen && (
          <div className={`weather-modal-overlay ${theme === 'dark' ? 'dark-overlay' : ''}`}>
            <div className="weather-modal-content mac-modal">
              <button className="weather-modal-close" onClick={closeModal}>×</button>
              <h2 className="weather-modal-title">ПОГОДА</h2>
              <p>Нет данных — введите город</p>
              <form className="weather-city-form" onSubmit={handleCitySubmit}>
                <input
                  type="text"
                  placeholder="Введите город..."
                  value={cityInput}
                  onChange={(e) => setCityInput(e.target.value)}
                />
                <button type="submit">OK</button>
              </form>
            </div>
          </div>
        )}
      </>
    );
  }

  const Icon = getWeatherIcon(weather.description);

  return (
    <>
      <div className="weather-widget mac-widget" onClick={handleWidgetClick}>
        <div className="weather-icon">
          <Icon />
        </div>
        <div className="weather-info">
          <div className="weather-temp">{weather.temp} °C</div>
          <div className="weather-city-small">{weather.name}</div>
        </div>
      </div>

      {isModalOpen && (
        <div className={`weather-modal-overlay ${theme === 'dark' ? 'dark-overlay' : ''}`}>
          <div className="weather-modal-content mac-modal">
            <button className="weather-modal-close" onClick={closeModal}>×</button>
            <h2 className="weather-modal-title">ПОГОДА</h2>

            <div className="weather-today">
              <div className="wt-icon">
                <Icon />
              </div>
              <div className="wt-temp">{weather.temp} °C</div>
              <div className="wt-city">{weather.name}</div>
              <div className="wt-desc">{weather.description}</div>
            </div>

            <form className="weather-city-form" onSubmit={handleCitySubmit}>
              <input
                type="text"
                placeholder="Введите город..."
                value={cityInput}
                onChange={(e) => setCityInput(e.target.value)}
              />
              <button type="submit">OK</button>
            </form>

            <hr />
            {forecast && forecast.length > 0 ? (
              <div className="weather-forecast">
                {forecast.map((day) => (
                  <div key={day.date} className="weather-forecast-day">
                    <div className="wf-dayName">
                      {new Date(day.date).toLocaleDateString('ru-RU', {
                        weekday: 'short',
                        day: 'numeric',
                        month: 'numeric',
                      })}
                    </div>
                    <div className="wf-temps">
                      <span className="wf-min">{day.min}°C</span> - <span className="wf-max">{day.max}°C</span>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p>Нет данных по прогнозу</p>
            )}
          </div>
        </div>
      )}
    </>
  );
}

export default WeatherWidget;
