import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import LinkBlock from './components/LinkBlock';
import SidebarMenu from './components/SidebarMenu';
import WeatherWidget from './components/WeatherWidget';
import ClockWidget from './components/ClockWidget';
import TodoWidget from './components/widgets/TodoWidget';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function App() {
  const [theme, setTheme] = useState('light');
  const [links, setLinks] = useState([]);
  const [isRemoveMode, setIsRemoveMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [sortBy, setSortBy] = useState('manual');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [editIndex, setEditIndex] = useState(null);

  // Фон
  const [customBackgroundType, setCustomBackgroundType] = useState('default'); // 'default' | 'image' | 'video'
  const [customBackgroundSrc, setCustomBackgroundSrc] = useState('');
  const [useDynamicTimeTheme, setUseDynamicTimeTheme] = useState(false);

  const fileInputRef = useRef(null);

  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) setTheme(storedTheme);

    const storedLinks = localStorage.getItem('userLinks');
    if (storedLinks) {
      setLinks(JSON.parse(storedLinks));
    } else {
      setLinks([
        { title: 'Google', url: 'https://www.google.com', customIcon: '', category: '', createdAt: Date.now() },
        { title: 'YouTube', url: 'https://www.youtube.com', customIcon: '', category: '', createdAt: Date.now() }
      ]);
    }

    // Фон
    const storedBgType = localStorage.getItem('bgType') || 'default';
    const storedBgSrc = localStorage.getItem('bgSrc') || '';
    const storedDynTime = localStorage.getItem('useDynamicTimeTheme') === 'true';

    setCustomBackgroundType(storedBgType);
    setCustomBackgroundSrc(storedBgSrc);
    setUseDynamicTimeTheme(storedDynTime);
  }, []);

  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    localStorage.setItem('userLinks', JSON.stringify(links));
  }, [links]);

  // Динамическая смена темы по времени
  useEffect(() => {
    if (!useDynamicTimeTheme) return;
    const now = new Date();
    const hour = now.getHours();
    if (hour >= 7 && hour < 19) {
      setTheme('light');
    } else {
      setTheme('dark');
    }
  }, [useDynamicTimeTheme]);

  const toggleTheme = () => {
    if (useDynamicTimeTheme) {
      alert('Включен режим автосмены темы по времени суток');
      return;
    }
    setTheme((prev) => (prev === 'light' ? 'dark' : 'light'));
  };

  const toggleRemoveMode = () => {
    setIsRemoveMode((prev) => !prev);
  };

  const handleAddLink = (newLink) => {
    const isDuplicate = links.some((link) => link.url === newLink.url);
    if (isDuplicate) {
      alert('Такая ссылка уже добавлена!');
      return;
    }
    setLinks((prevLinks) => [...prevLinks, newLink]);
  };

  const handleRemoveLink = (index) => {
    setLinks((prevLinks) => prevLinks.filter((_, i) => i !== index));
  };

  const handleMenuToggle = () => {
    setIsMenuOpen((prev) => !prev);
  };

  const handleEditLink = (index) => {
    setEditIndex(index);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
      fileInputRef.current.click();
    }
  };

  const handleSelectFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      const base64Data = evt.target.result;
      setLinks((prevLinks) => {
        const updated = [...prevLinks];
        updated[editIndex] = {
          ...updated[editIndex],
          customIcon: base64Data
        };
        return updated;
      });
    };
    reader.readAsDataURL(file);
  };

  const filteredLinks =
    selectedCategory === 'All'
      ? links
      : links.filter((link) => link.category === selectedCategory);

  const sortedLinks = [...filteredLinks];
  if (sortBy === 'alphabetical') {
    sortedLinks.sort((a, b) => a.title.localeCompare(b.title, 'ru', { sensitivity: 'base' }));
  } else if (sortBy === 'date') {
    sortedLinks.sort((a, b) => b.createdAt - a.createdAt);
  }

  const onDragEnd = (result) => {
    if (!result.destination) return;
    if (sortBy !== 'manual') return;
    if (selectedCategory !== 'All') return;
    const fromIndex = result.source.index;
    const toIndex = result.destination.index;
    const movingItem = sortedLinks[fromIndex];
    const newLinks = Array.from(links);
    const globalIndex = links.findIndex((l) => l === movingItem);
    newLinks.splice(globalIndex, 1);
    const targetItem = sortedLinks[toIndex];
    const globalIndexTarget = links.findIndex((l) => l === targetItem);
    newLinks.splice(globalIndexTarget, 0, movingItem);
    setLinks(newLinks);
  };

  // ======  ФУНКЦИОНАЛ ДЛЯ СМЕНЫ ФОНА (локальные файлы) ======
  const handleLocalBackgroundFile = (file) => {
    if (!file) return;

    // Проверяем тип (image/* или video/*)
    if (file.type.startsWith('image/')) {
      // Картинка
      const reader = new FileReader();
      reader.onload = () => {
        const result = reader.result;
        setCustomBackgroundType('image');
        setCustomBackgroundSrc(result);
        localStorage.setItem('bgType', 'image');
        localStorage.setItem('bgSrc', result);
      };
      reader.readAsDataURL(file);
    } else if (file.type.startsWith('video/')) {
      // Видео
      const reader = new FileReader();
      reader.onload = () => {
        const result = reader.result;
        setCustomBackgroundType('video');
        setCustomBackgroundSrc(result);
        localStorage.setItem('bgType', 'video');
        localStorage.setItem('bgSrc', result);
      };
      reader.readAsDataURL(file);
    } else {
      alert('Файл не является картинкой или видео');
    }
  };

  // Сброс фона
  const resetBackground = () => {
    setCustomBackgroundType('default');
    setCustomBackgroundSrc('');
    localStorage.setItem('bgType', 'default');
    localStorage.removeItem('bgSrc');
  };


  const renderLinks = () => {
    return sortedLinks.map((link, index) => (
      <Draggable
        key={index}
        draggableId={`link-${index}`}
        index={index}
        isDragDisabled={sortBy !== 'manual' || selectedCategory !== 'All'}
      >
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
          >
            <LinkBlock
              key={index}
              title={link.title}
              url={link.url}
              customIcon={link.customIcon}
              isRemoveMode={isRemoveMode}
              onRemove={() => handleRemoveLink(index)}
              onEdit={() => handleEditLink(index)}
            />
          </div>
        )}
      </Draggable>
    ));
  };

  // Рендер фона
  let backgroundElement = null;
  if (customBackgroundType === 'image' && customBackgroundSrc) {
    backgroundElement = (
      <div
        className="custom-bg-image"
        style={{ backgroundImage: `url(${customBackgroundSrc})` }}
      />
    );
  } else if (customBackgroundType === 'video' && customBackgroundSrc) {
    backgroundElement = (
      <video className="custom-bg-video" autoPlay loop muted>
        <source src={customBackgroundSrc} type="video/mp4" />
      </video>
    );
  }

  return (
    <div className={`App ${theme}`}>
      {backgroundElement}

      <button className="menu-button" onClick={handleMenuToggle}>
        ☰
      </button>

      <SidebarMenu
        isOpen={isMenuOpen}
        onClose={handleMenuToggle}
        onAddLink={handleAddLink}
        toggleTheme={toggleTheme}
        toggleRemoveMode={toggleRemoveMode}
        sortBy={sortBy}
        setSortBy={setSortBy}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
        useDynamicTimeTheme={useDynamicTimeTheme}
        setUseDynamicTimeTheme={setUseDynamicTimeTheme}
        onLocalBackgroundFile={handleLocalBackgroundFile}
        resetBackground={resetBackground}
      />

      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        accept="image/*,video/*"
        onChange={handleSelectFile}
      />

      <main>
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="links-droppable" direction="horizontal">
            {(provided) => (
              <section
                className="links-section"
                ref={provided.innerRef}
                {...provided.droppableProps}
              >
                {renderLinks()}
                {provided.placeholder}
              </section>
            )}
          </Droppable>
        </DragDropContext>

        <section className="widgets-section">
          <WeatherWidget theme={theme} />
          <ClockWidget />
          <TodoWidget theme={theme} />
        </section>
      </main>
    </div>
  );
}

export default App;
