import React, { useState } from 'react';
import './LinkBlock.css';
// import defaultIcon from '../assets/default-icon.png'; // Иконка по умолчанию (создайте или подключите)

function LinkBlock({ title, url, isRemoveMode, onRemove, customIcon }) {
  let domain = '';
  try {
    domain = new URL(url).hostname;
  } catch (error) {
    // URL некорректный
  }

  // Если пользователь указал customIcon, используем её.
  // Иначе используем Google Favicons
  const googleIconUrl = `https://www.google.com/s2/favicons?domain=${domain}&sz=128`;
  const initialIcon = customIcon ? customIcon : googleIconUrl;

  // Локальное состояние для иконки
  const [iconSrc, setIconSrc] = useState(initialIcon);

  // Функция fallback на случай ошибки загрузки
  const handleImgError = () => {
    setIconSrc(defaultIcon); // подставляем иконку по умолчанию
  };

  return (
    <div className="link-block-wrapper">
      <a
        href={url}
        className="link-block"
        target="_blank"
        rel="noopener noreferrer"
      >
        <div className="icon-wrapper">
          <img
            src={iconSrc}
            alt="favicon"
            className="favicon"
            onError={handleImgError}
          />
        </div>
        <div className="tooltip">{title}</div>
      </a>

      {/* Кнопка удаления, если включён режим isRemoveMode */}
      {isRemoveMode && (
        <button className="remove-block-btn" onClick={onRemove}>
          -
        </button>
      )}
    </div>
  );
}

export default LinkBlock;
