import React, { useState, useEffect, useRef } from 'react';
import { FiMoreHorizontal } from 'react-icons/fi';
import './App.css';

import LinkBlock from './components/LinkBlock';
import SidebarMenu from './components/SidebarMenu';
import WeatherWidget from './components/WeatherWidget';
import ClockWidget from './components/ClockWidget';
import TodoWidget from './components/widgets/TodoWidget';

import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function groupBy(array, keyFn) {
  const result = {};
  array.forEach((item) => {
    const key = keyFn(item);
    if (!result[key]) {
      result[key] = [];
    }
    result[key].push(item);
  });
  return result;
}

function App() {
  const [theme, setTheme] = useState('light');
  const [links, setLinks] = useState([]);
  const [isRemoveMode, setIsRemoveMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Сортировка / Категория (как прежде)
  const [sortBy, setSortBy] = useState('manual');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const [editIndex, setEditIndex] = useState(null);
  const fileInputRef = useRef(null);

  // Папка, открытая сейчас
  const [folderOpenCat, setFolderOpenCat] = useState(null);

  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) setTheme(storedTheme);

    const storedLinks = localStorage.getItem('userLinks');
    if (storedLinks) {
      setLinks(JSON.parse(storedLinks));
    } else {
      setLinks([
        {
          title: 'Google',
          url: 'https://www.google.com',
          customIcon: '',
          category: 'favorite',
          createdAt: Date.now(),
        },
        {
          title: 'YouTube',
          url: 'https://www.youtube.com',
          customIcon: '',
          category: 'video',
          createdAt: Date.now(),
        },
      ]);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    localStorage.setItem('userLinks', JSON.stringify(links));
  }, [links]);

  useEffect(() => {
    document.documentElement.classList.remove('light','dark');
    document.documentElement.classList.add(theme);
  }, [theme]);

  // ==== FUNCTIONS ====
  const toggleTheme = () => {
    setTheme((prev) => (prev === 'light' ? 'dark' : 'light'));
  };

  const toggleRemoveMode = () => {
    setIsRemoveMode((prev) => !prev);
  };

  const handleAddLink = (newLink) => {
    const isDuplicate = links.some((l) => l.url === newLink.url);
    if (isDuplicate) {
      alert('Такая ссылка уже добавлена!');
      return;
    }
    setLinks((prev) => [...prev, newLink]);
  };

  const handleRemoveLink = (index) => {
    setLinks((prev) => prev.filter((_, i) => i !== index));
  };

  const handleMenuToggle = () => {
    setIsMenuOpen((prev) => !prev);
  };

  const handleEditLink = (index) => {
    setEditIndex(index);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
      fileInputRef.current.click();
    }
  };

  const handleSelectFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      const base64Data = evt.target.result;
      setLinks((prev) => {
        const updated = [...prev];
        updated[editIndex] = {
          ...updated[editIndex],
          customIcon: base64Data,
        };
        return updated;
      });
    };
    reader.readAsDataURL(file);
  };

  // ==== FILTERING / SORTING
  let filteredLinks;
  if (selectedCategory === 'All') {
    filteredLinks = links;
  } else {
    // Если выбрана какая-то категория, + показываем favorite
    filteredLinks = links.filter(
      (l) => l.category === 'favorite' || l.category === selectedCategory
    );
  }

  const sortedLinks = [...filteredLinks];
  if (sortBy === 'alphabetical') {
    sortedLinks.sort((a, b) =>
      a.title.localeCompare(b.title, 'ru', { sensitivity: 'base' })
    );
  } else if (sortBy === 'date') {
    sortedLinks.sort((a, b) => b.createdAt - a.createdAt);
  }
  // manual => drag & drop

  // Drag & Drop
  const onDragEnd = (result) => {
    if (!result.destination) return;
    // source { droppableId, index }, destination { droppableId, index }
    const { source, destination } = result;

    // Если сортировка не manual, игнорируем
    if (sortBy !== 'manual') return;

    // Если user перетаскивает в ту же категорию / droppable
    if (source.droppableId === destination.droppableId) {
      // reorder внутри той же droppable
      reorderInSameDroppable(source.droppableId, source.index, destination.index);
    } else {
      // Если не хотим меж-дроппл перенос, можно игнорировать
      // ИЛИ поддержать перенос? По задаче не было явно
      // Сейчас просто игнорируем
      return;
    }
  };

  // РЕОРДЕР при перетаскивании внутри одной droppable
  function reorderInSameDroppable(droppableId, fromIndex, toIndex) {
    // droppableId может быть "favorites", "cat:video", "folder:video"
    // будем хранить всё в links, нужно найти нужную группу

    setLinks((prevLinks) => {
      // Копию массив
      const newLinks = [...prevLinks];
      // Определяем массив ссылок, которые сейчас видит droppable
      // favorites => l.category === 'favorite'
      // cat:NAME => l.category === NAME, но без папки?
      // folder:NAME => l.category === NAME, все?

      let droppableFilter;
      if (droppableId === 'favorites') {
        droppableFilter = (l) => l.category === 'favorite';
      } else if (droppableId.startsWith('folder:')) {
        // папка
        const catName = droppableId.replace('folder:', '');
        droppableFilter = (l) => l.category === catName;
      } else if (droppableId.startsWith('cat:')) {
        // категория
        const catName = droppableId.replace('cat:', '');
        droppableFilter = (l) => l.category === catName;
      } else {
        // неизвестно
        return newLinks;
      }

      // Получаем список ссылок, которые в этой droppable
      const droppableLinks = newLinks.filter(droppableFilter);

      // Надо учесть, что если это категория, мы показываем первые 3
      // + 4-й = папка (не двигается).
      // => reorder только первые 3
      let maxCount = droppableLinks.length; 
      let skipFolder = false;

      // Если droppableId начинается с 'cat:' => 
      // ... у нас может быть папка, которая 4-я, 
      // но она не reorderable. 
      // => reorder только 3
      if (droppableId.startsWith('cat:') && droppableLinks.length > 3) {
        maxCount = 3; 
        skipFolder = true;
      }

      // reorder
      if (fromIndex >= maxCount || toIndex >= maxCount) {
        // Нельзя двинуть/target > 3 => игнорируем
        return newLinks;
      }

      // droppableLinksIndices хранит индексы в global newLinks
      const droppableLinksIndices = droppableLinks.map((x) => newLinks.indexOf(x));

      // itemIndexInDroppable => droppableLinksIndices[fromIndex]
      const itemGlobalIndex = droppableLinksIndices[fromIndex];

      // убираем
      newLinks.splice(itemGlobalIndex, 1);

      // targetIndexInGlobal = droppableLinksIndices[toIndex], 
      //  но после удаления itemGlobalIndex меняется
      // лучше аккуратно вставить

      // Если fromIndex < toIndex => globalIndex toIndex + 1
      // Но проще: уберём item, потом droppableLinksIndices[toIndex] - 
      //   потому что этот массив тоже изменился 
      //   => проще recalulate droppableLinksIndices?
      //   ... 

      // Легче сделать reorder локально, потом слияние

      const subArr = droppableLinks.slice(0, maxCount); 
      // remove item from subArr
      const [removed] = subArr.splice(fromIndex, 1);
      subArr.splice(toIndex, 0, removed);

      // Теперь subArr - это новый порядок (для первых 3),
      // если skipFolder, нужно append folder
      if (skipFolder && droppableLinks.length > 3) {
        subArr.push(droppableLinks[3]); 
      }
      // Остальные, если >4, subArr.push(...theRest)? 
      // Но на UI показываем только 4 anyway.

      // Теперь соберём назад
      // droppableLinks => subArr + theRest (которое >4)
      // TheRest
      if (droppableLinks.length > 4 && skipFolder) {
        // 5+ -> subArr + droppableLinks.slice(4)
        subArr.push(...droppableLinks.slice(4));
      }

      // subArr теперь это reordered droppableLinks
      // заменим в newLinks
      // Удалим все droppableFilter
      const newArrWithoutThisDroppable = newLinks.filter(
        (l) => !droppableFilter(l)
      );
      // добавим subArr (reordered) 
      const merged = [...newArrWithoutThisDroppable, ...subArr];

      return merged;
    });
  }

  // Собираем full sorted links
  const favoriteLinks = sortedLinks.filter((l) => l.category === 'favorite');
  const otherLinks = sortedLinks.filter((l) => l.category !== 'favorite');

  // Группируем otherLinks
  const grouped = groupBy(otherLinks, (l) => l.category || 'Без категории');
  const groupedCategories = Object.keys(grouped).map((catName) => ({
    name: catName,
    items: grouped[catName],
  }));

  // DRAGGABLES
  function renderFavoriteLinks() {
    return favoriteLinks.map((link, index) => (
      <Draggable
        key={`fav-${index}`}
        draggableId={`fav-${index}`}
        index={index}
        isDragDisabled={sortBy !== 'manual'}
      >
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            style={provided.draggableProps.style}
          >
            <LinkBlock
              title={link.title}
              url={link.url}
              customIcon={link.customIcon}
              isRemoveMode={isRemoveMode}
              onRemove={() => {
                const globalIndex = links.indexOf(link);
                if (globalIndex >= 0) handleRemoveLink(globalIndex);
              }}
              onEdit={() => {
                const globalIndex = links.indexOf(link);
                if (globalIndex >= 0) handleEditLink(globalIndex);
              }}
            />
          </div>
        )}
      </Draggable>
    ));
  }

  function renderCategoryDroppable(catBlock) {
    // catBlock.name, catBlock.items
    // droppableId => `cat:${catBlock.name}`
    // 1) firstThree draggable, 4th is folder if hasMore
    // The rest hidden
    const firstThree = catBlock.items.slice(0, 3);
    const hasMore = catBlock.items.length > 3;

    return (
      <Droppable key={catBlock.name} droppableId={`cat:${catBlock.name}`} direction="horizontal">
        {(provided) => (
          <div className="category-section">
            <h3>{catBlock.name}</h3>
            <div
              className="category-links"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              {firstThree.map((link, idx) => (
                <Draggable
                  key={`cat-${catBlock.name}-${idx}`}
                  draggableId={`cat-${catBlock.name}-${idx}`}
                  index={idx}
                  // Folder is not in these 3
                  isDragDisabled={sortBy !== 'manual'}
                >
                  {(providedDrag) => (
                    <div
                      ref={providedDrag.innerRef}
                      {...providedDrag.draggableProps}
                      {...providedDrag.dragHandleProps}
                      style={providedDrag.draggableProps.style}
                    >
                      <LinkBlock
                        title={link.title}
                        url={link.url}
                        customIcon={link.customIcon}
                        isRemoveMode={isRemoveMode}
                        onRemove={() => {
                          const globalIndex = links.indexOf(link);
                          if (globalIndex >= 0) handleRemoveLink(globalIndex);
                        }}
                        onEdit={() => {
                          const globalIndex = links.indexOf(link);
                          if (globalIndex >= 0) handleEditLink(globalIndex);
                        }}
                      />
                    </div>
                  )}
                </Draggable>
              ))}

              {hasMore && (
                <div
                  className="link-block-wrapper folder-block"
                  onClick={() => setFolderOpenCat(catBlock.name)}
                >
                  <div className="link-block folder-block-inner">
                    <div className="icon-wrapper">
                      <FiMoreHorizontal className="folder-more-icon" />
                    </div>
                    <div className="tooltip">Показать ещё</div>
                  </div>
                </div>
              )}
              {provided.placeholder}
            </div>
          </div>
        )}
      </Droppable>
    );
  }

  function renderCategories() {
    return groupedCategories.map((catBlock) => renderCategoryDroppable(catBlock));
  }

  // Папка -> droppable => folder:catName
  function renderFolderModal() {
    if (!folderOpenCat) return null;
    const openFolderItems = groupedCategories.find(
      (c) => c.name === folderOpenCat
    )?.items || [];

    return (
      <div className="folder-modal-overlay" onClick={() => setFolderOpenCat(null)}>
        <div className="folder-modal-content" onClick={(e) => e.stopPropagation()}>
          <button className="folder-close" onClick={() => setFolderOpenCat(null)}>
            ×
          </button>
          <h2>Категория: {folderOpenCat}</h2>

          <Droppable droppableId={`folder:${folderOpenCat}`} direction="horizontal">
            {(provided) => (
              <div className="folder-links" ref={provided.innerRef} {...provided.droppableProps}>
                {openFolderItems.map((link, idx) => (
                  <Draggable
                    key={`folder-${folderOpenCat}-${idx}`}
                    draggableId={`folder-${folderOpenCat}-${idx}`}
                    index={idx}
                    isDragDisabled={sortBy !== 'manual'}
                  >
                    {(providedDrag) => (
                      <div
                        ref={providedDrag.innerRef}
                        {...providedDrag.draggableProps}
                        {...providedDrag.dragHandleProps}
                        style={providedDrag.draggableProps.style}
                      >
                        <LinkBlock
                          title={link.title}
                          url={link.url}
                          customIcon={link.customIcon}
                          isRemoveMode={isRemoveMode}
                          onRemove={() => {
                            const globalIndex = links.indexOf(link);
                            if (globalIndex >= 0) handleRemoveLink(globalIndex);
                          }}
                          onEdit={() => {
                            const globalIndex = links.indexOf(link);
                            if (globalIndex >= 0) handleEditLink(globalIndex);
                          }}
                        />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </div>
    );
  }

  // Список категорий (exclude favorite if you want)
  const allCategories = [
    'All',
    ...new Set(links
      .map((l) => l.category)
      .filter((c) => c && c !== 'favorite')
    ),
  ];

  return (
    <div className={`App ${theme}`}>
      <div>
        <input
          type="checkbox"
          id="checkbox"
          className="menu-btn"
          onClick={handleMenuToggle}
        />
        <label htmlFor="checkbox" className="toggle">
          <div className="bars" id="bar1" />
          <div className="bars" id="bar2" />
          <div className="bars" id="bar3" />
        </label>
      </div>

      <SidebarMenu
        isOpen={isMenuOpen}
        onClose={handleMenuToggle}
        onAddLink={handleAddLink}
        toggleTheme={toggleTheme}
        toggleRemoveMode={toggleRemoveMode}
      />

      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        accept="image/*"
        onChange={handleSelectFile}
      />

      <main>
        <DragDropContext onDragEnd={onDragEnd}>
          {/* Избранное */}
          <Droppable droppableId="favorites" direction="horizontal">
            {(provided) => (
              <section className="favorites-row" ref={provided.innerRef} {...provided.droppableProps}>
                <h2>Избранные</h2>
                <div className="favorites-links">
                  {renderFavoriteLinks()}
                  {provided.placeholder}
                </div>
              </section>
            )}
          </Droppable>

          {/* Панель сортировки / категории */}
          <section className="sorting-category-panel">
            <div className="sort-block">
              <h4>Сортировка</h4>
              <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                <option value="manual">Ручная (Drag & Drop)</option>
                <option value="alphabetical">По алфавиту</option>
                <option value="date">По дате (новые сверху)</option>
              </select>
            </div>

            <div className="category-block">
              <h4>Категория</h4>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {allCategories.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>
          </section>

          {/* Сетка категорий */}
          <section className="categories-grid">
            {renderCategories()}
          </section>
        </DragDropContext>

        <section className="widgets-section">
          <WeatherWidget theme={theme} />
          <ClockWidget />
          <TodoWidget theme={theme} />
        </section>
      </main>

      {/* Модальное окно папки */}
      {renderFolderModal()}
    </div>
  );
}

export default App;
