import React, { useState, useEffect } from 'react';
import './TodoWidget.css';

function TodoWidget({ theme }) {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const storedTasks = localStorage.getItem('todoTasks');
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('todoTasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    if (inputValue.trim() === '') return;
    const newTask = {
      id: Date.now(),
      text: inputValue.trim(),
      completed: false
    };
    setTasks((prev) => [...prev, newTask]);
    setInputValue('');
  };

  const toggleTask = (id) => {
    setTasks((prev) =>
      prev.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const removeTask = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const openModal = () => {
    setIsModalOpen(true);
  };
  
  const closeModal = () => {
    setIsModalOpen(false);
  };

  const firstTwoTasks = tasks.slice(0, 2);

  return (
    <div className="todo-widget widget">
      <div className="todo-small-title">
        СПИСОК ДЕЛ
      </div>
      <ul className="todo-small-list">
        {firstTwoTasks.map((task) => (
          <li key={task.id} className={task.completed ? 'completed' : ''}>
            {task.text}
          </li>
        ))}
      </ul>
      {tasks.length > 2 && (
        <button className="todo-all-btn" onClick={openModal}>
          Все задачи
        </button>
      )}
      {tasks.length <= 2 && (
        <button className="todo-all-btn" onClick={openModal}>
          Редактировать
        </button>
      )}

      {isModalOpen && (
        <div className={`modal-overlay ${theme === 'dark' ? 'dark-overlay' : ''}`}>
          <div className="modal-content">
            <button className="close-modal" onClick={closeModal}>
              ×
            </button>
            <h2 className="modal-title">Список дел</h2>
            <div className="modal-input-row">
              <input
                type="text"
                placeholder="Новая задача..."
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
              />
              <button className="add-task-btn" onClick={addTask}>+</button>
            </div>
            <hr />
            <ul className="modal-todo-list">
              {tasks.map((task) => (
                <li key={task.id} className={task.completed ? 'completed' : ''}>
                  <span onClick={() => toggleTask(task.id)}>{task.text}</span>
                  <button className="remove-todo" onClick={() => removeTask(task.id)}>
                    ×
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default TodoWidget;
