import React, { useState, useEffect } from 'react';
import './LinkBlock.css';
import defaultIcon from '../assets/default-icon.png';

const googleIconUrl = `https://www.google.com/s2/favicons?domain=${domain}&sz=128`;
const initialIcon = customIcon ? customIcon : googleIconUrl;

function LinkBlock({ 
  title, 
  url, 
  customIcon = '', 
  isRemoveMode, 
  onRemove, 
  onEdit 
}) {
  let domain = '';
  try {
    domain = new URL(url).hostname;
  } catch (error) {}

  const googleIconUrl = `https://www.google.com/s2/favicons?domain=${domain}&sz=128`;

  // Выбираем, что показать как иконку: если customIcon непустой — берём его,
  // иначе — Google Favicons
  const [iconSrc, setIconSrc] = useState(customIcon || googleIconUrl);

  // Если customIcon сменился (пользователь загрузил новую картинку),
  // обновим локальное состояние, чтобы <img> сразу подхватил
  useEffect(() => {
    if (customIcon) {
      setIconSrc(customIcon);
    } else {
      setIconSrc(googleIconUrl);
    }
  }, [customIcon, googleIconUrl]);

  const handleImgError = () => {
    setIconSrc(defaultIcon);
  };

  return (
    <div className="link-block-wrapper">
      <a
        href={url}
        className="link-block"
        target="_blank"
        rel="noopener noreferrer"
      >
        <div className="icon-wrapper">
        <img
          src={iconSrc}
          alt="icon"
          className="favicon"
          onError={handleImgError}
        />
        </div>
        <div className="tooltip">{title}</div>
      </a>

      {isRemoveMode && (
        <>
          {/* Кнопка удаления (справа сверху) */}
          <button className="remove-block-btn" onClick={onRemove}>
            -
          </button>
          {/* Кнопка редактирования (слева сверху) */}
          <button className="edit-block-btn" onClick={onEdit}>
            ✎
          </button>
        </>
      )}
    </div>
  );
}

export default LinkBlock;
