import React, { useState, useEffect } from 'react';
import './App.css';
import LinkBlock from './components/LinkBlock';
import LinkForm from './components/LinkForm';
import WeatherWidget from './components/WeatherWidget';
import ClockWidget from './components/ClockWidget';

function App() {
  const [theme, setTheme] = useState('light');
  const [links, setLinks] = useState([]);

  // 1. При монтировании загружаем тему из localStorage
  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) {
      setTheme(storedTheme);
    }
  }, []);

  // 2. При каждом изменении темы, сохраняем её в localStorage
  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  // Аналогично для ссылок — загружаем из localStorage и сохраняем
  useEffect(() => {
    const storedLinks = localStorage.getItem('userLinks');
    if (storedLinks) {
      setLinks(JSON.parse(storedLinks));
    } else {
      // Если данных нет, можно установить дефолтные ссылки
      setLinks([
        { title: 'Google', url: 'https://www.google.com' },
        { title: 'YouTube', url: 'https://www.youtube.com' }
      ]);
    }
  }, []);

  // Сохраняем обновлённый список ссылок при изменении
  useEffect(() => {
    localStorage.setItem('userLinks', JSON.stringify(links));
  }, [links]);

  // Переключатель темы
  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // Добавление новой ссылки
  const handleAddLink = (newLink) => {
    setLinks(prevLinks => [...prevLinks, newLink]);
  };

  return (
    <div className={`App ${theme}`}>
      <header className="header">
        <button onClick={toggleTheme} className="theme-toggle">
          Переключить тему
        </button>
      </header>

      <main>
        <section className="link-form-section">
          <LinkForm onAddLink={handleAddLink} />
        </section>

        <section className="links-section">
          {links.map((link, index) => (
            <LinkBlock key={index} title={link.title} url={link.url} />
          ))}
        </section>

        <section className="widgets-section">
          <WeatherWidget />
          <ClockWidget />
        </section>
      </main>
    </div>
  );
}

export default App;
