import React, { useState, useEffect } from 'react';
import './WeatherWidget.css';

function WeatherWidget() {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [city, setCity] = useState(''); // Можно дать пользователю возможность ввести город

  // Замените на свой ключ, полученный на https://openweathermap.org/
  const API_KEY = 'YOUR_OPENWEATHERMAP_API_KEY';

  useEffect(() => {
    const savedCity = localStorage.getItem('weatherCity');
    if (savedCity) {
      setCity(savedCity);
      fetchWeather(savedCity);
    } else {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('weatherCity', city);
  }, [city]);

  const fetchWeather = async (cityName) => {
    try {
      setLoading(true);
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(
          cityName
        )}&units=metric&lang=ru&appid=${API_KEY}`
      );
      if (!response.ok) throw new Error('Город не найден');
      const data = await response.json();
      setWeather({
        temp: Math.round(data.main.temp),
        description: data.weather[0].description,
        name: data.name
      });
      setLoading(false);
    } catch (error) {
      alert(error.message);
      setLoading(false);
      setWeather(null);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!city.trim()) return;
    fetchWeather(city.trim());
  };

  return (
    <div className="weather-widget widget">
      <h3>Погода</h3>
      <form onSubmit={handleSubmit} className="weather-form">
        <input
          type="text"
          placeholder="Введите город..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button type="submit">OK</button>
      </form>

      {loading && <p>Загрузка...</p>}
      {!loading && weather && (
        <div className="weather-info">
          <p>{weather.name}</p>
          <p>{weather.temp}°C</p>
          <p>{weather.description}</p>
        </div>
      )}
      {!loading && !weather && (
        <p>Введите город, чтобы узнать погоду</p>
      )}
    </div>
  );
}

export default WeatherWidget;
