import React, { useState } from 'react';
import './LinkForm.css';

function LinkForm({ onAddLink }) {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');
  const [iconBase64, setIconBase64] = useState('');
  const [category, setCategory] = useState(''); // Новое поле категории

  // ...функции isValidUrl, fetchWithTimeout, handleFileChange и т.д.

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Проверки title, url, проверка доступности сайта — как у вас уже сделано
    // Если всё ОК, вызываем onAddLink
    onAddLink({
      title,
      url,
      customIcon: iconBase64 || '',
      category: category.trim(),  // Сохраняем категорию
      createdAt: Date.now(),      // Фиксируем дату добавления (timestamp)
    });

    // Сброс формы
    setTitle('');
    setUrl('');
    setIconBase64('');
    setCategory('');
    e.target.reset();
  };

  return (
    <form onSubmit={handleSubmit} className="link-form">
      {/* Поля Название, URL, загрузка иконки... */}
      
      <input
        type="text"
        placeholder="Категория (например, 'Работа', 'Игры')"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />

      <button type="submit">Добавить ссылку</button>
    </form>
  );
}

export default LinkForm;
