import React, { useState, useEffect } from 'react';
import './TodoWidget.css';

function TodoWidget({ theme }) {
  // Основные состояния
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [priority, setPriority] = useState('low'); // low, medium, high
  const [deadline, setDeadline] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [filter, setFilter] = useState('all'); // 'all' | 'done' | 'todo'

  useEffect(() => {
    const storedTasks = localStorage.getItem('todoTasks');
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('todoTasks', JSON.stringify(tasks));
  }, [tasks]);

  // Добавление задачи
  const addTask = () => {
    if (inputValue.trim() === '') return;
    const newTask = {
      id: Date.now(),
      text: inputValue.trim(),
      completed: false,
      priority,
      deadline // строка вида "YYYY-MM-DD"
    };
    setTasks((prev) => [...prev, newTask]);
    setInputValue('');
    setPriority('low');
    setDeadline('');
  };

  // Переключение статуса "выполнено"
  const toggleTask = (id) => {
    setTasks((prev) =>
      prev.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  // Удаление задачи
  const removeTask = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  // Открыть модалку
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Закрыть модалку (при клике на крестик)
  const closeModal = (e) => {
    e.stopPropagation();
    setIsModalOpen(false);
  };

  // Клик по самому блоку виджета
  const handleWidgetClick = () => {
    openModal();
  };

  // Фильтрация
  let filteredTasks = [...tasks];
  if (filter === 'done') {
    filteredTasks = filteredTasks.filter((t) => t.completed);
  } else if (filter === 'todo') {
    filteredTasks = filteredTasks.filter((t) => !t.completed);
  }

  // Сортируем по приоритету (high > medium > low),
  // при одинаковом приоритете — по ближайшему дедлайну
  filteredTasks.sort((a, b) => {
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    // Сравниваем приоритет
    if (priorityOrder[b.priority] !== priorityOrder[a.priority]) {
      return priorityOrder[b.priority] - priorityOrder[a.priority];
    }
    // Если приоритеты равны — сортируем по дедлайну (чем раньше дата, тем выше)
    if (a.deadline && b.deadline) {
      return new Date(a.deadline) - new Date(b.deadline);
    }
    // Если у одного дедлайн есть, у другого нет — тот что с дедлайном выше
    if (a.deadline && !b.deadline) return -1;
    if (!a.deadline && b.deadline) return 1;
    return 0;
  });

  // Для отображения первых 2 задач на виджете
  const firstTwoTasks = filteredTasks.slice(0, 2);

  return (
    <>
      <div className="todo-widget widget" onClick={handleWidgetClick}>
        <div className="todo-small-title">СПИСОК ДЕЛ</div>
        <ul className="todo-small-list">
          {firstTwoTasks.map((task) => (
            <li key={task.id} className={task.completed ? 'completed' : ''}>
              {task.text}
            </li>
          ))}
        </ul>
      </div>

      {isModalOpen && (
        <div className={`modal-overlay ${theme === 'dark' ? 'dark-overlay' : ''}`}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-modal" onClick={closeModal}>
              ×
            </button>
            <h2 className="modal-title">Список дел</h2>

            {/* Блок ввода новой задачи */}
            <div className="modal-input-row">
              <input
                type="text"
                placeholder="Новая задача..."
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
              />
              <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                <option value="low">Низкий приоритет</option>
                <option value="medium">Средний приоритет</option>
                <option value="high">Высокий приоритет</option>
              </select>
              <input
                type="date"
                value={deadline}
                onChange={(e) => setDeadline(e.target.value)}
              />
              <button className="add-task-btn" onClick={addTask}>
                +
              </button>
            </div>

            {/* Фильтр */}
            <div className="todo-filter">
              <button
                className={filter === 'all' ? 'active-filter' : ''}
                onClick={() => setFilter('all')}
              >
                Все
              </button>
              <button
                className={filter === 'done' ? 'active-filter' : ''}
                onClick={() => setFilter('done')}
              >
                Выполнено
              </button>
              <button
                className={filter === 'todo' ? 'active-filter' : ''}
                onClick={() => setFilter('todo')}
              >
                Осталось
              </button>
            </div>

            <hr />

            {/* Список задач (уже отсортированный и отфильтрованный) */}
            <ul className="modal-todo-list">
              {filteredTasks.map((task) => (
                <li
                  key={task.id}
                  className={task.completed ? 'completed' : ''}
                >
                  <span onClick={() => toggleTask(task.id)}>
                    [{task.priority === 'high' ? '!!!' : task.priority === 'medium' ? '!!' : '!'}]
                    &nbsp;{task.text}
                  </span>
                  {task.deadline && (
                    <span className="deadline-badge">
                      {new Date(task.deadline).toLocaleDateString('ru-RU')}
                    </span>
                  )}
                  <button
                    className="remove-todo"
                    onClick={() => removeTask(task.id)}
                  >
                    ×
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </>
  );
}

export default TodoWidget;
