import React, { useState } from 'react';
import LinkForm from './LinkForm';
import './SidebarMenu.css';

function SidebarMenu({ 
  isOpen, 
  onClose, 
  onAddLink, 
  toggleTheme, 
  toggleRemoveMode 
}) {
  const [showForm, setShowForm] = useState(false);

  const handleAddBlockClick = () => {
    // По клику переключаем видимость формы
    setShowForm(prev => !prev);
  };

  return (
    <div className={`sidebar-menu ${isOpen ? 'open' : ''}`}>
      {/* Кнопка для закрытия меню (если нужно) */}
      <button className="close-button" onClick={onClose}>
        &times;
      </button>

      <h2>Меню</h2>

      {/* Кнопка «Добавить блок» */}
      <button onClick={handleAddBlockClick}>
        Добавить блок
      </button>

      {/* Показываем форму, если showForm = true */}
      {showForm && (
        <div className="add-form-wrapper">
          <LinkForm onAddLink={onAddLink} />
        </div>
      )}

      {/* Смена темы */}
      <button onClick={toggleTheme}>
        Смена темы
      </button>

      {/* Изменение блока (режим удаления) */}
      <button onClick={toggleRemoveMode}>
        Изменение блока
      </button>
    </div>
  );
}

export default SidebarMenu;
