import React, { useState } from 'react';
import './LinkForm.css';

function LinkForm({ onAddLink }) {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  // Функция проверки валидности URL
  const isValidUrl = (value) => {
    try {
      new URL(value);
      return true;
    } catch (error) {
      return false;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (title.trim() === '' || url.trim() === '') {
      return;
    }
    // Если URL не валиден – выводим предупреждение и не добавляем блок
    if (!isValidUrl(url)) {
      alert('Пожалуйста, введите корректный URL, например, https://example.com');
      return;
    }
    onAddLink({ title, url });
    setTitle('');
    setUrl('');
  };

  return (
    <form onSubmit={handleSubmit} className="link-form">
      <input
        type="text"
        placeholder="Название ссылки"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <input
        type="url"
        placeholder="URL (например, https://example.com)"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        required
      />
      <button type="submit">Добавить ссылку</button>
    </form>
  );
}

export default LinkForm;
