import React, { useState } from 'react';
import './LinkForm.css';

function LinkForm({ onAddLink }) {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');
  const [category, setCategory] = useState('');
  
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim() || !url.trim()) return;

    // Создаём объект ссылки
    const newLink = {
      title,
      url,
      category: category.trim() || 'Без категории', // по умолчанию
      dateAdded: new Date().toISOString(),          // сохраняем дату
      customIcon: ''
    };

    onAddLink(newLink);

    // Сброс полей
    setTitle('');
    setUrl('');
    setCategory('');
  };

  return (
    <form onSubmit={handleSubmit} className="link-form">
      <input
        type="text"
        placeholder="Название ссылки"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <input
        type="url"
        placeholder="URL (например, https://example.com)"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Категория (напр. Рабочие)"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />
      <button type="submit">Добавить ссылку</button>
    </form>
  );
}

export default LinkForm;
