import React, { useState } from 'react';
import LinkForm from './LinkForm';
import './SidebarMenu.css';
import BackgroundModal from './modals/BackgroundModal';

function SidebarMenu({
  isOpen,
  onClose,
  onAddLink,
  toggleTheme,
  toggleRemoveMode,
  sortBy,
  setSortBy,
  selectedCategory,
  setSelectedCategory,
  useDynamicTimeTheme,
  setUseDynamicTimeTheme,
  onLocalBackgroundFile,
  resetBackground
}) {
  const [showForm, setShowForm] = useState(false);
  const [showBgModal, setShowBgModal] = useState(false);

  const handleAddBlockClick = () => {
    setShowForm((prev) => !prev);
  };

  const handleOpenBgModal = () => {
    setShowBgModal(true);
  };
  const handleCloseBgModal = () => {
    setShowBgModal(false);
  };

  return (
    <div className={`sidebar-menu ${isOpen ? 'open' : ''}`}>
      <button className="close-button" onClick={onClose}>
        &times;
      </button>
      <h3>Меню</h3>

      <button onClick={handleAddBlockClick}>Добавить блок</button>
      {showForm && (
        <div className="add-form-wrapper">
          <LinkForm onAddLink={onAddLink} />
        </div>
      )}

      <button onClick={toggleTheme}>Смена темы</button>
      <button onClick={toggleRemoveMode}>Изменение блока</button>

      <div className="sidebar-section">
        <h4>Сортировка</h4>
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="manual">Ручная</option>
          <option value="alphabetical">По алфавиту</option>
          <option value="date">По дате</option>
        </select>
      </div>

      <div className="sidebar-section">
        <h4>Категория</h4>
        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="All">Все</option>
          {/* Если у вас есть динамические категории ссылок, можно вывести их тут */}
        </select>
      </div>

      <button onClick={handleOpenBgModal}>Фон / Тема</button>

      <BackgroundModal
        show={showBgModal}
        onClose={handleCloseBgModal}
        useDynamicTimeTheme={useDynamicTimeTheme}
        setUseDynamicTimeTheme={setUseDynamicTimeTheme}
        onLocalBackgroundFile={onLocalBackgroundFile}
        resetBackground={resetBackground}
      />
    </div>
  );
}

export default SidebarMenu;
