import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import LinkBlock from './components/LinkBlock';
import WeatherWidget from './components/WeatherWidget';
import ClockWidget from './components/ClockWidget';
import SidebarMenu from './components/SidebarMenu';

function App() {
  const [theme, setTheme] = useState('light');
  const [links, setLinks] = useState([]);
  const [isRemoveMode, setIsRemoveMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Храним индекс того блока, который сейчас редактируем (иконку)
  const [editIndex, setEditIndex] = useState(null);

  // Ссылка на скрытый input для выбора файла
  const fileInputRef = useRef(null);

  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) setTheme(storedTheme);
  }, []);

  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    const storedLinks = localStorage.getItem('userLinks');
    if (storedLinks) {
      setLinks(JSON.parse(storedLinks));
    } else {
      setLinks([
        { title: 'Google', url: 'https://www.google.com', customIcon: '' },
        { title: 'YouTube', url: 'https://www.youtube.com', customIcon: '' }
      ]);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('userLinks', JSON.stringify(links));
  }, [links]);

  const toggleTheme = () => {
    setTheme(prev => (prev === 'light' ? 'dark' : 'light'));
  };

  // Переключение режима удаления/редактирования
  const toggleRemoveMode = () => {
    setIsRemoveMode(prev => !prev);
  };

  // Добавление новой ссылки
  const handleAddLink = (newLink) => {
    const isDuplicate = links.some(link => link.url === newLink.url);
    if (isDuplicate) {
      alert('Такая ссылка уже добавлена!');
      return;
    }
    setLinks(prevLinks => [...prevLinks, newLink]);
  };

  // Удаление ссылки
  const handleRemoveLink = (index) => {
    setLinks(prevLinks => prevLinks.filter((_, i) => i !== index));
  };

  // Когда пользователь кликает «Редактировать иконку» на блоке
  const handleEditLink = (index) => {
    // Запоминаем, какой блок редактируем
    setEditIndex(index);
    // Программно «кликаем» по скрытому input
    if (fileInputRef.current) {
      fileInputRef.current.value = ''; // Сбрасываем старый выбор
      fileInputRef.current.click();
    }
  };

  // Когда пользователь выбрал файл
  const handleSelectFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Считываем файл как base64
    const reader = new FileReader();
    reader.onload = (evt) => {
      const base64Data = evt.target.result; 
      // Обновляем customIcon в массиве links
      setLinks(prevLinks => {
        const updated = [...prevLinks];
        updated[editIndex] = {
          ...updated[editIndex],
          customIcon: base64Data
        };
        return updated;
      });
    };
    reader.readAsDataURL(file);
  };

  // Открыть/Закрыть меню
  const handleMenuToggle = () => {
    setIsMenuOpen(prev => !prev);
  };

  return (
    <div className={`App ${theme}`}>
      <button className="menu-button" onClick={handleMenuToggle}>
        ☰
      </button>

      <SidebarMenu
        isOpen={isMenuOpen}
        onClose={handleMenuToggle}
        onAddLink={handleAddLink}
        toggleTheme={toggleTheme}
        toggleRemoveMode={toggleRemoveMode}
      />

      {/* Скрытый input для выбора файла. onChange вызывается при выборе файла */}
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        accept="image/*"
        onChange={handleSelectFile}
      />

      <main>
        <section className="links-section">
          {links.map((link, index) => (
            <LinkBlock
              key={index}
              title={link.title}
              url={link.url}
              customIcon={link.customIcon}
              isRemoveMode={isRemoveMode}
              onRemove={() => handleRemoveLink(index)}
              onEdit={() => handleEditLink(index)} // передаём функцию
            />
          ))}
        </section>

        <section className="widgets-section">
          <WeatherWidget />
          <ClockWidget />
        </section>
      </main>
    </div>
  );
}

export default App;
